contracts/AgentMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./WorldTreasury.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AgentMarketplace
 * @dev Production-grade P2P marketplace for agent economy
 * 
 * Features:
 * - Reentrancy protection on all transactions
 * - Pausable for emergency stops
 * - Custom errors for gas optimization
 * - Listing expiration system
 * - Marketplace fees
 * - Comprehensive input validation
 * 
 * @custom:security-contact security@diem.xyz
 */
contract AgentMarketplace is Ownable, ReentrancyGuard, Pausable {
    
    // ============ Constants ============
    
    /// @notice Maximum listing duration (30 days)
    uint256 public constant MAX_LISTING_DURATION = 30 days;
    
    /// @notice Minimum price to prevent spam (0.1 MON)
    uint256 public constant MIN_PRICE = 0.1 * 10**18;
    
    /// @notice Maximum profit share percentage
    uint256 public constant MAX_PROFIT_SHARE = 50;
    
    // ============ State Variables ============
    
    /// @notice WorldTreasury contract
    WorldTreasury public immutable treasury;
    
    /// @notice MON token contract
    MONToken public immutable monToken;
    
    /// @notice Marketplace fee percentage (basis points, e.g., 250 = 2.5%)
    uint256 public marketplaceFeePercent = 250; // 2.5%
    
    /// @notice Total marketplace fees collected
    uint256 public totalFeesCollected;
    
    /// @notice Listing counter
    uint256 public listingCounter;
    
    /// @notice Service counter
    uint256 public serviceCounter;
    
    /// @notice Investment counter
    uint256 public investmentCounter;
    
    // ============ Structs ============
    
    /// @notice Listing data (optimized storage)
    struct Listing {
        address seller;          // 20 bytes
        uint96 pricePerUnit;     // 12 bytes (fits in slot 1 with address)
        uint128 quantity;        // 16 bytes (slot 2)
        uint128 createdAt;       // 16 bytes (fits in slot 2)
        string itemType;         // Dynamic (slot 3+)
        bool active;             // 1 byte (slot N)
    }
    
    /// @notice Service offer data
    struct ServiceOffer {
        address provider;        // 20 bytes
        uint96 pricePerAction;   // 12 bytes
        uint128 completedJobs;   // 16 bytes
        bool active;             // 1 byte
        string serviceType;      // Dynamic
    }
    
    /// @notice Investment data
    struct Investment {
        address investor;        // 20 bytes
        address agent;           // 20 bytes
        uint96 amount;           // 12 bytes
        uint32 profitShare;      // 4 bytes (0-100)
        uint128 timestamp;       // 16 bytes
        bool active;             // 1 byte
    }
    
    // ============ Mappings ============
    
    mapping(uint256 => Listing) public listings;
    mapping(uint256 => ServiceOffer) public services;
    mapping(uint256 => Investment) public investments;
    mapping(address => uint256[]) private agentListings;
    mapping(address => uint256[]) private agentServices;
    mapping(address => uint256) public totalInvested;
    
    // ============ Custom Errors ============
    
    error InvalidQuantity();
    error InvalidPrice();
    error InvalidAddress();
    error InvalidProfitShare();
    error ListingNotActive();
    error ServiceNotActive();
    error InvestmentNotActive();
    error InsufficientQuantity();
    error InsufficientBalance();
    error CannotBuyOwnListing();
    error CannotBuyOwnService();
    error CannotInvestInSelf();
    error TransferFailed();
    error ListingExpired();
    error NotListingOwner();
    error NotServiceOwner();
    error NotInvestmentAgent();
    error InvalidFeePercent();
    
    // ============ Events ============
    
    event ItemListed(uint256 indexed listingId, address indexed seller, string itemType, uint256 quantity, uint256 price, uint256 expiresAt);
    event ItemPurchased(uint256 indexed listingId, address indexed buyer, address indexed seller, uint256 quantity, uint256 totalPrice, uint256 fee);
    event ListingCancelled(uint256 indexed listingId, address indexed seller);
    event ServiceOffered(uint256 indexed serviceId, address indexed provider, string serviceType, uint256 price);
    event ServicePurchased(uint256 indexed serviceId, address indexed buyer, address indexed provider, uint256 price, uint256 fee);
    event ServiceCancelled(uint256 indexed serviceId, address indexed provider);
    event InvestmentMade(uint256 indexed investmentId, address indexed investor, address indexed agent, uint256 amount, uint256 profitShare);
    event ProfitDistributed(uint256 indexed investmentId, address indexed investor, uint256 profit);
    event InvestmentWithdrawn(uint256 indexed investmentId, address indexed investor, uint256 amount);
    event MarketplaceFeeUpdated(uint256 oldFee, uint256 newFee);
    event FeesWithdrawn(address indexed recipient, uint256 amount);
    
    // ============ Constructor ============
    
    /**
     * @notice Initializes the AgentMarketplace
     * @param _treasury Address of WorldTreasury contract
     * @param _monToken Address of MON token contract
     */
    constructor(address _treasury, address _monToken) Ownable(msg.sender) {
        if (_treasury == address(0) || _monToken == address(0)) revert InvalidAddress();
        treasury = WorldTreasury(_treasury);
        monToken = MONToken(_monToken);
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Updates marketplace fee percentage
     * @param newFeePercent New fee in basis points (max 1000 = 10%)
     */
    function setMarketplaceFee(uint256 newFeePercent) external onlyOwner {
        if (newFeePercent > 1000) revert InvalidFeePercent(); // Max 10%
        uint256 oldFee = marketplaceFeePercent;
        marketplaceFeePercent = newFeePercent;
        emit MarketplaceFeeUpdated(oldFee, newFeePercent);
    }
    
    /**
     * @notice Withdraws collected marketplace fees
     * @param recipient Address to receive fees
     */
    function withdrawFees(address recipient) external onlyOwner {
        if (recipient == address(0)) revert InvalidAddress();
        uint256 amount = totalFeesCollected;
        totalFeesCollected = 0;
        
        if (!monToken.transfer(recipient, amount)) revert TransferFailed();
        emit FeesWithdrawn(recipient, amount);
    }
    
    /**
     * @notice Pauses all marketplace operations
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpauses marketplace operations
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    // ============ Item Listing Functions ============
    
    /**
     * @notice Lists an item for sale
     * @param itemType Type of item
     * @param quantity Quantity to sell
     * @param pricePerUnit Price per unit in MON
     * @return listingId ID of the created listing
     */
    function listItem(string calldata itemType, uint256 quantity, uint256 pricePerUnit) 
        external 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        if (quantity == 0) revert InvalidQuantity();
        if (pricePerUnit < MIN_PRICE) revert InvalidPrice();
        
        uint256 listingId = listingCounter++;
        
        listings[listingId] = Listing({
            seller: msg.sender,
            itemType: itemType,
            quantity: uint128(quantity),
            pricePerUnit: uint96(pricePerUnit),
            active: true,
            createdAt: uint128(block.timestamp)
        });
        
        agentListings[msg.sender].push(listingId);
        
        uint256 expiresAt = block.timestamp + MAX_LISTING_DURATION;
        emit ItemListed(listingId, msg.sender, itemType, quantity, pricePerUnit, expiresAt);
        
        return listingId;
    }
    
    /**
     * @notice Purchases items from a listing
     * @param listingId ID of the listing
     * @param quantity Quantity to purchase
     */
    function purchaseItem(uint256 listingId, uint256 quantity) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Listing storage listing = listings[listingId];
        
        if (!listing.active) revert ListingNotActive();
        if (listing.quantity < quantity) revert InsufficientQuantity();
        if (msg.sender == listing.seller) revert CannotBuyOwnListing();
        
        // Check expiration
        if (block.timestamp > listing.createdAt + MAX_LISTING_DURATION) {
            listing.active = false;
            revert ListingExpired();
        }
        
        uint256 totalPrice = uint256(listing.pricePerUnit) * quantity;
        if (monToken.balanceOf(msg.sender) < totalPrice) revert InsufficientBalance();
        
        // Calculate marketplace fee
        uint256 fee = (totalPrice * marketplaceFeePercent) / 10000;
        uint256 sellerAmount = totalPrice - fee;
        
        // Transfer payment
        if (!monToken.transferFrom(msg.sender, listing.seller, sellerAmount)) revert TransferFailed();
        if (fee > 0) {
            if (!monToken.transferFrom(msg.sender, address(this), fee)) revert TransferFailed();
            unchecked {
                totalFeesCollected += fee;
            }
        }
        
        // Update listing
        unchecked {
            listing.quantity -= uint128(quantity);
        }
        if (listing.quantity == 0) {
            listing.active = false;
        }
        
        emit ItemPurchased(listingId, msg.sender, listing.seller, quantity, totalPrice, fee);
    }
    
    /**
     * @notice Cancels a listing
     * @param listingId ID of the listing to cancel
     */
    function cancelListing(uint256 listingId) external {
        Listing storage listing = listings[listingId];
        if (listing.seller != msg.sender) revert NotListingOwner();
        listing.active = false;
        emit ListingCancelled(listingId, msg.sender);
    }
    
    // ============ Service Functions ============
    
    /**
     * @notice Offers a service
     * @param serviceType Type of service
     * @param pricePerAction Price per action in MON
     * @return serviceId ID of the created service
     */
    function offerService(string calldata serviceType, uint256 pricePerAction) 
        external 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        if (pricePerAction < MIN_PRICE) revert InvalidPrice();
        
        uint256 serviceId = serviceCounter++;
        
        services[serviceId] = ServiceOffer({
            provider: msg.sender,
            serviceType: serviceType,
            pricePerAction: uint96(pricePerAction),
            active: true,
            completedJobs: 0
        });
        
        agentServices[msg.sender].push(serviceId);
        
        emit ServiceOffered(serviceId, msg.sender, serviceType, pricePerAction);
        return serviceId;
    }
    
    /**
     * @notice Purchases a service
     * @param serviceId ID of the service
     */
    function purchaseService(uint256 serviceId) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        ServiceOffer storage service = services[serviceId];
        
        if (!service.active) revert ServiceNotActive();
        if (msg.sender == service.provider) revert CannotBuyOwnService();
        
        uint256 price = service.pricePerAction;
        if (monToken.balanceOf(msg.sender) < price) revert InsufficientBalance();
        
        // Calculate marketplace fee
        uint256 fee = (price * marketplaceFeePercent) / 10000;
        uint256 providerAmount = price - fee;
        
        // Transfer payment
        if (!monToken.transferFrom(msg.sender, service.provider, providerAmount)) revert TransferFailed();
        if (fee > 0) {
            if (!monToken.transferFrom(msg.sender, address(this), fee)) revert TransferFailed();
            unchecked {
                totalFeesCollected += fee;
            }
        }
        
        unchecked {
            service.completedJobs++;
        }
        
        emit ServicePurchased(serviceId, msg.sender, service.provider, price, fee);
    }
    
    /**
     * @notice Cancels a service offer
     * @param serviceId ID of the service to cancel
     */
    function cancelService(uint256 serviceId) external {
        ServiceOffer storage service = services[serviceId];
        if (service.provider != msg.sender) revert NotServiceOwner();
        service.active = false;
        emit ServiceCancelled(serviceId, msg.sender);
    }
    
    // ============ Investment Functions ============
    
    /**
     * @notice Invests in an agent
     * @param agent Address of agent to invest in
     * @param amount Amount of MON to invest
     * @param profitShare Profit share percentage (1-50)
     * @return investmentId ID of the investment
     */
    function investInAgent(address agent, uint256 amount, uint256 profitShare) 
        external 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        if (agent == msg.sender) revert CannotInvestInSelf();
        if (agent == address(0)) revert InvalidAddress();
        if (amount == 0) revert InvalidPrice();
        if (profitShare == 0 || profitShare > MAX_PROFIT_SHARE) revert InvalidProfitShare();
        if (monToken.balanceOf(msg.sender) < amount) revert InsufficientBalance();
        
        // Transfer investment to agent
        if (!monToken.transferFrom(msg.sender, agent, amount)) revert TransferFailed();
        
        uint256 investmentId = investmentCounter++;
        
        investments[investmentId] = Investment({
            investor: msg.sender,
            agent: agent,
            amount: uint96(amount),
            timestamp: uint128(block.timestamp),
            profitShare: uint32(profitShare),
            active: true
        });
        
        unchecked {
            totalInvested[agent] += amount;
        }
        
        emit InvestmentMade(investmentId, msg.sender, agent, amount, profitShare);
        return investmentId;
    }
    
    /**
     * @notice Distributes profit to investor
     * @param investmentId ID of the investment
     * @param profit Amount of profit to distribute
     */
    function distributeProfitToInvestor(uint256 investmentId, uint256 profit) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Investment storage investment = investments[investmentId];
        
        if (!investment.active) revert InvestmentNotActive();
        if (msg.sender != investment.agent) revert NotInvestmentAgent();
        if (monToken.balanceOf(msg.sender) < profit) revert InsufficientBalance();
        
        // Transfer profit share
        if (!monToken.transferFrom(msg.sender, investment.investor, profit)) revert TransferFailed();
        
        emit ProfitDistributed(investmentId, investment.investor, profit);
    }
    
    /**
     * @notice Withdraws investment (by investor)
     * @param investmentId ID of the investment
     */
    function withdrawInvestment(uint256 investmentId) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Investment storage investment = investments[investmentId];
        
        if (!investment.active) revert InvestmentNotActive();
        if (msg.sender != investment.investor) revert NotListingOwner();
        
        investment.active = false;
        
        unchecked {
            totalInvested[investment.agent] -= investment.amount;
        }
        
        emit InvestmentWithdrawn(investmentId, msg.sender, investment.amount);
    }
    
    // ============ View Functions ============
    
    /**
     * @notice Gets agent's active listings
     * @param agent Address of the agent
     * @return Array of listing IDs
     */
    function getAgentListings(address agent) external view returns (uint256[] memory) {
        return agentListings[agent];
    }
    
    /**
     * @notice Gets agent's active services
     * @param agent Address of the agent
     * @return Array of service IDs
     */
    function getAgentServices(address agent) external view returns (uint256[] memory) {
        return agentServices[agent];
    }
    
    /**
     * @notice Gets marketplace statistics
     * @return totalListings Total number of listings created
     * @return totalServices Total number of services offered
     * @return totalInvestments Total number of investments made
     * @return feesCollected Total marketplace fees collected
     */
    function getMarketplaceStats() external view returns (
        uint256 totalListings,
        uint256 totalServices,
        uint256 totalInvestments,
        uint256 feesCollected
    ) {
        return (listingCounter, serviceCounter, investmentCounter, totalFeesCollected);
    }
}

contracts/MONToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title MON Token
 * @dev Production-grade ERC20 token for Diem virtual world on Monad blockchain
 * 
 * Features:
 * - Reentrancy protection on all state-changing functions
 * - Pausable for emergency circuit breaker
 * - Custom errors for gas optimization
 * - Comprehensive events for transparency
 * - Role-based access control
 * 
 * @custom:security-contact security@diem.xyz
 */
contract MONToken is ERC20, Ownable, ReentrancyGuard, Pausable {
    
    // ============ Constants ============
    
    /// @notice Entry fee required to join the world
    uint256 public constant ENTRY_FEE = 100 * 10**18; // 100 MON
    
    /// @notice Initial balance given to new agents for testing
    uint256 public constant INITIAL_AGENT_BALANCE = 200 * 10**18; // 200 MON
    
    /// @notice Maximum reward that can be minted in a single transaction
    uint256 public constant MAX_SINGLE_REWARD = 1000 * 10**18; // 1000 MON
    
    /// @notice Initial supply minted to deployer
    uint256 private constant INITIAL_SUPPLY = 1000000 * 10**18; // 1M MON
    
    // ============ State Variables ============
    
    /// @notice Address of the WorldTreasury contract authorized to mint rewards
    address public worldTreasury;
    
    /// @notice Total amount of rewards minted
    uint256 public totalRewardsMinted;
    
    /// @notice Total number of agents funded
    uint256 public totalAgentsFunded;
    
    // ============ Custom Errors ============
    
    /// @dev Thrown when caller is not the WorldTreasury
    error OnlyTreasury();
    
    /// @dev Thrown when treasury address is zero
    error InvalidTreasuryAddress();
    
    /// @dev Thrown when agent address is zero
    error InvalidAgentAddress();
    
    /// @dev Thrown when reward amount exceeds maximum
    error RewardTooLarge(uint256 requested, uint256 maximum);
    
    /// @dev Thrown when agent array is empty
    error EmptyAgentArray();
    
    /// @dev Thrown when batch operation is too large
    error BatchTooLarge(uint256 size, uint256 maximum);
    
    // ============ Events ============
    
    /// @notice Emitted when an agent is funded with initial balance
    /// @param agent Address of the funded agent
    /// @param amount Amount of MON tokens funded
    event AgentFunded(address indexed agent, uint256 amount);
    
    /// @notice Emitted when a reward is minted
    /// @param agent Address receiving the reward
    /// @param amount Amount of MON tokens minted
    /// @param totalMinted Total rewards minted so far
    event RewardMinted(address indexed agent, uint256 amount, uint256 totalMinted);
    
    /// @notice Emitted when WorldTreasury address is updated
    /// @param oldTreasury Previous treasury address
    /// @param newTreasury New treasury address
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    
    // ============ Constructor ============
    
    /**
     * @notice Initializes the MON token with initial supply
     * @dev Mints initial supply to the contract deployer
     */
    constructor() ERC20("Monad Token", "MON") Ownable(msg.sender) {
        _mint(msg.sender, INITIAL_SUPPLY);
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Sets the WorldTreasury contract address
     * @dev Only owner can call. Treasury address cannot be zero.
     * @param _treasury Address of the WorldTreasury contract
     */
    function setWorldTreasury(address _treasury) external onlyOwner {
        if (_treasury == address(0)) revert InvalidTreasuryAddress();
        
        address oldTreasury = worldTreasury;
        worldTreasury = _treasury;
        
        emit TreasuryUpdated(oldTreasury, _treasury);
    }
    
    /**
     * @notice Pauses all token transfers
     * @dev Only owner can call. Use in emergency situations.
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpauses token transfers
     * @dev Only owner can call.
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    // ============ Agent Funding Functions ============
    
    /**
     * @notice Funds a new agent with initial MON balance
     * @dev Only owner can call. Protected against reentrancy.
     * @param agent Address of the agent to fund
     */
    function fundAgent(address agent) external onlyOwner nonReentrant whenNotPaused {
        if (agent == address(0)) revert InvalidAgentAddress();
        
        _mint(agent, INITIAL_AGENT_BALANCE);
        
        unchecked {
            // Safe: totalAgentsFunded will never realistically overflow
            ++totalAgentsFunded;
        }
        
        emit AgentFunded(agent, INITIAL_AGENT_BALANCE);
    }
    
    /**
     * @notice Batch funds multiple agents with initial MON balance
     * @dev Only owner can call. Gas-optimized for batch operations.
     * @param agents Array of agent addresses to fund
     */
    function fundAgents(address[] calldata agents) external onlyOwner nonReentrant whenNotPaused {
        uint256 length = agents.length;
        if (length == 0) revert EmptyAgentArray();
        if (length > 100) revert BatchTooLarge(length, 100);
        
        for (uint256 i = 0; i < length;) {
            address agent = agents[i];
            if (agent == address(0)) revert InvalidAgentAddress();
            
            _mint(agent, INITIAL_AGENT_BALANCE);
            emit AgentFunded(agent, INITIAL_AGENT_BALANCE);
            
            unchecked {
                // Safe: i will never overflow in realistic scenarios
                ++i;
            }
        }
        
        unchecked {
            // Safe: totalAgentsFunded will never realistically overflow
            totalAgentsFunded += length;
        }
    }
    
    // ============ Reward Functions ============
    
    /**
     * @notice Mints reward tokens for agent actions
     * @dev Only WorldTreasury can call. Protected against reentrancy.
     * @param agent Address of the agent receiving the reward
     * @param amount Amount of MON tokens to mint
     */
    function mintReward(address agent, uint256 amount) external nonReentrant whenNotPaused {
        if (msg.sender != worldTreasury) revert OnlyTreasury();
        if (agent == address(0)) revert InvalidAgentAddress();
        if (amount > MAX_SINGLE_REWARD) revert RewardTooLarge(amount, MAX_SINGLE_REWARD);
        
        _mint(agent, amount);
        
        unchecked {
            // Safe: totalRewardsMinted overflow would require unrealistic token amounts
            totalRewardsMinted += amount;
        }
        
        emit RewardMinted(agent, amount, totalRewardsMinted);
    }
    
    // ============ View Functions ============
    
    /**
     * @notice Returns token statistics
     * @return supply Total supply of MON tokens
     * @return rewardsMinted Total rewards minted
     * @return agentsFunded Total agents funded
     */
    function getTokenStats() external view returns (
        uint256 supply,
        uint256 rewardsMinted,
        uint256 agentsFunded
    ) {
        return (totalSupply(), totalRewardsMinted, totalAgentsFunded);
    }
    
    // ============ Internal Overrides ============
    
    /**
     * @dev Hook that is called before any transfer of tokens
     * @param from Address tokens are transferred from
     * @param to Address tokens are transferred to
     * @param amount Amount of tokens transferred
     */
    function _update(address from, address to, uint256 amount) 
        internal 
        override 
        whenNotPaused 
    {
        super._update(from, to, amount);
    }
}

contracts/WorldBoss.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title WorldBoss
 * @notice Manages world boss encounters requiring agent coordination
 * @dev The Titan - A massive boss that requires 5+ agents to defeat
 */
contract WorldBoss is Ownable, ReentrancyGuard, Pausable {
    // Custom errors for gas efficiency
    error BossNotActive();
    error BossAlreadyDefeated();
    error InsufficientDamage();
    error NotParticipant();
    error RewardAlreadyClaimed();
    error BossStillAlive();
    error TooSoonToRespawn();

    struct Boss {
        uint256 currentHealth;
        uint256 maxHealth;
        uint256 prizePool;
        uint256 spawnTime;
        uint256 defeatTime;
        bool isActive;
        bool isDefeated;
        address[] participants;
        mapping(address => uint256) damageDealt;
        mapping(address => bool) rewardClaimed;
    }

    // State variables
    Boss public currentBoss;
    uint256 public bossCounter;
    uint256 public constant RESPAWN_COOLDOWN = 2 hours;
    uint256 public constant MIN_PARTICIPANTS = 5;
    uint256 public constant BOSS_MAX_HEALTH = 10000;
    uint256 public constant MIN_DAMAGE = 10;
    uint256 public constant MAX_DAMAGE = 100;

    // Events
    event BossSpawned(uint256 indexed bossId, uint256 maxHealth, uint256 spawnTime);
    event BossAttacked(uint256 indexed bossId, address indexed attacker, uint256 damage, uint256 remainingHealth);
    event BossDefeated(uint256 indexed bossId, uint256 defeatTime, uint256 prizePool, uint256 participantCount);
    event RewardClaimed(uint256 indexed bossId, address indexed participant, uint256 reward);
    event PrizePoolIncreased(uint256 indexed bossId, uint256 amount, uint256 newTotal);

    constructor() Ownable(msg.sender) {
        _spawnBoss();
    }

    /**
     * @notice Attack the current boss
     * @param damage Amount of damage to deal (will be clamped to MIN_DAMAGE - MAX_DAMAGE)
     */
    function attackBoss(uint256 damage) external nonReentrant whenNotPaused {
        if (!currentBoss.isActive) revert BossNotActive();
        if (currentBoss.isDefeated) revert BossAlreadyDefeated();

        // Clamp damage to valid range
        uint256 actualDamage = damage;
        if (actualDamage < MIN_DAMAGE) actualDamage = MIN_DAMAGE;
        if (actualDamage > MAX_DAMAGE) actualDamage = MAX_DAMAGE;

        // Track participant
        if (currentBoss.damageDealt[msg.sender] == 0) {
            currentBoss.participants.push(msg.sender);
        }
        currentBoss.damageDealt[msg.sender] += actualDamage;

        // Apply damage
        if (actualDamage >= currentBoss.currentHealth) {
            currentBoss.currentHealth = 0;
            currentBoss.isDefeated = true;
            currentBoss.defeatTime = block.timestamp;
            
            emit BossDefeated(
                bossCounter,
                block.timestamp,
                currentBoss.prizePool,
                currentBoss.participants.length
            );
        } else {
            currentBoss.currentHealth -= actualDamage;
        }

        emit BossAttacked(bossCounter, msg.sender, actualDamage, currentBoss.currentHealth);
    }

    /**
     * @notice Claim reward after boss is defeated
     */
    function claimReward() external nonReentrant {
        if (!currentBoss.isDefeated) revert BossStillAlive();
        if (currentBoss.damageDealt[msg.sender] == 0) revert NotParticipant();
        if (currentBoss.rewardClaimed[msg.sender]) revert RewardAlreadyClaimed();

        // Calculate reward based on damage contribution
        uint256 totalDamage = BOSS_MAX_HEALTH;
        uint256 userDamage = currentBoss.damageDealt[msg.sender];
        uint256 reward = (currentBoss.prizePool * userDamage) / totalDamage;

        currentBoss.rewardClaimed[msg.sender] = true;

        // Transfer reward
        (bool success, ) = msg.sender.call{value: reward}("");
        require(success, "Reward transfer failed");

        emit RewardClaimed(bossCounter, msg.sender, reward);
    }

    /**
     * @notice Spawn a new boss (only after cooldown)
     */
    function spawnBoss() external onlyOwner {
        if (currentBoss.isActive && !currentBoss.isDefeated) revert BossNotActive();
        if (block.timestamp < currentBoss.defeatTime + RESPAWN_COOLDOWN) revert TooSoonToRespawn();

        _spawnBoss();
    }

    /**
     * @notice Add funds to the current boss prize pool
     */
    function increasePrizePool() external payable {
        if (!currentBoss.isActive) revert BossNotActive();
        
        currentBoss.prizePool += msg.value;
        emit PrizePoolIncreased(bossCounter, msg.value, currentBoss.prizePool);
    }

    /**
     * @notice Get boss status
     */
    function getBossStatus() external view returns (
        uint256 health,
        uint256 maxHealth,
        uint256 prizePool,
        uint256 participantCount,
        bool isActive,
        bool isDefeated,
        uint256 spawnTime
    ) {
        return (
            currentBoss.currentHealth,
            currentBoss.maxHealth,
            currentBoss.prizePool,
            currentBoss.participants.length,
            currentBoss.isActive,
            currentBoss.isDefeated,
            currentBoss.spawnTime
        );
    }

    /**
     * @notice Get participant damage
     */
    function getParticipantDamage(address participant) external view returns (uint256) {
        return currentBoss.damageDealt[participant];
    }

    /**
     * @notice Get all participants
     */
    function getParticipants() external view returns (address[] memory) {
        return currentBoss.participants;
    }

    /**
     * @notice Internal function to spawn boss
     */
    function _spawnBoss() private {
        bossCounter++;
        
        // Reset boss state
        delete currentBoss.participants;
        currentBoss.currentHealth = BOSS_MAX_HEALTH;
        currentBoss.maxHealth = BOSS_MAX_HEALTH;
        currentBoss.prizePool = 0;
        currentBoss.spawnTime = block.timestamp;
        currentBoss.defeatTime = 0;
        currentBoss.isActive = true;
        currentBoss.isDefeated = false;

        emit BossSpawned(bossCounter, BOSS_MAX_HEALTH, block.timestamp);
    }

    /**
     * @notice Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Receive ETH
     */
    receive() external payable {
        if (currentBoss.isActive) {
            currentBoss.prizePool += msg.value;
            emit PrizePoolIncreased(bossCounter, msg.value, currentBoss.prizePool);
        }
    }
}

contracts/WorldTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./MONToken.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title WorldTreasury
 * @dev Production-grade treasury managing entry fees, rewards, and agent economy
 * 
 * Features:
 * - Reentrancy protection on all state-changing functions
 * - Pausable for emergency stops
 * - Custom errors for gas optimization
 * - Comprehensive input validation
 * - Rate limiting for reward distribution
 * - Agent reputation tracking
 * 
 * @custom:security-contact security@diem.xyz
 */
contract WorldTreasury is Ownable, ReentrancyGuard, Pausable {
    // ============ Constants ============
    
    /// @notice Entry fee required to join the world
    uint256 public constant ENTRY_FEE = 100 * 10**18;
    
    /// @notice Maximum reward per action to prevent exploits
    uint256 public constant MAX_REWARD_PER_ACTION = 500 * 10**18;
    
    /// @notice Minimum time between rewards for same agent (anti-spam)
    uint256 public constant REWARD_COOLDOWN = 1 seconds;
    
    // ============ State Variables ============
    
    /// @notice MON token contract
    MONToken public immutable monToken;
    
    /// @notice Total number of agents who have entered
    uint256 public totalAgents;
    
    /// @notice Total entry fees collected
    uint256 public totalFeesCollected;
    
    /// @notice Total rewards distributed
    uint256 public totalRewardsDistributed;
    
    /// @notice Authorized reward distributors
    mapping(address => bool) public isDistributor;
    
    /// @notice Agent data structure (optimized for storage)
    struct Agent {
        bool hasEntered;           // 1 byte
        uint248 entryTime;         // 31 bytes (fits with bool in one slot)
        uint256 totalEarned;       // 32 bytes (slot 2)
        uint256 totalSpent;        // 32 bytes (slot 3)
        uint256 actionCount;       // 32 bytes (slot 4)
        uint256 lastRewardTime;    // 32 bytes (slot 5) - for rate limiting
        uint256 reputationScore;   // 32 bytes (slot 6) - for future features
    }
    
    /// @notice Agent data by address
    mapping(address => Agent) public agents;
    
    /// @notice Total transfers between agent pairs
    mapping(address => mapping(address => uint256)) public agentToAgentTransfers;
    
    // ============ Custom Errors ============
    
    error AgentAlreadyEntered();
    error AgentNotInWorld();
    error InsufficientBalance();
    error InvalidAmount();
    error InvalidAddress();
    error RewardTooLarge(uint256 requested, uint256 maximum);
    error RewardCooldownActive(uint256 timeRemaining);
    error TransferFailed();
    error NotAuthorizedDistributor();
    
    // ============ Events ============
    
    event AgentEntered(address indexed agent, uint256 entryFee, uint256 timestamp);
    event RewardDistributed(address indexed agent, uint256 amount, string reason, uint256 newBalance);
    event AgentToAgentTransfer(address indexed from, address indexed to, uint256 amount, string reason);
    event ItemPurchased(address indexed buyer, address indexed seller, uint256 price, string itemType);
    event DistributorUpdated(address indexed distributor, bool authorized);
    event ReputationUpdated(address indexed agent, uint256 oldScore, uint256 newScore);
    
    // ============ Constructor ============
    
    /**
     * @notice Initializes the WorldTreasury
     * @param _monToken Address of the MON token contract
     */
    constructor(address _monToken) Ownable(msg.sender) {
        if (_monToken == address(0)) revert InvalidAddress();
        monToken = MONToken(_monToken);
        
        // Owner is automatically a distributor
        isDistributor[msg.sender] = true;
        emit DistributorUpdated(msg.sender, true);
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Authorizes or revokes a reward distributor
     * @param distributor Address to update
     * @param authorized Whether the address is authorized
     */
    function setDistributor(address distributor, bool authorized) external onlyOwner {
        if (distributor == address(0)) revert InvalidAddress();
        isDistributor[distributor] = authorized;
        emit DistributorUpdated(distributor, authorized);
    }
    
    /**
     * @notice Pauses all treasury operations
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpauses treasury operations
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    // ============ Core Functions ============
    
    /**
     * @notice Agent pays entry fee to join the world
     * @dev Protected against reentrancy. Requires approval of ENTRY_FEE tokens.
     */
    function enterWorld() external nonReentrant whenNotPaused {
        if (agents[msg.sender].hasEntered) revert AgentAlreadyEntered();
        if (monToken.balanceOf(msg.sender) < ENTRY_FEE) revert InsufficientBalance();
        
        // Transfer entry fee to treasury
        if (!monToken.transferFrom(msg.sender, address(this), ENTRY_FEE)) {
            revert TransferFailed();
        }
        
        agents[msg.sender] = Agent({
            hasEntered: true,
            entryTime: uint248(block.timestamp),
            totalEarned: 0,
            totalSpent: ENTRY_FEE,
            actionCount: 0,
            lastRewardTime: 0,
            reputationScore: 100 // Starting reputation
        });
        
        unchecked {
            // Safe: totalAgents and totalFeesCollected won't realistically overflow
            ++totalAgents;
            totalFeesCollected += ENTRY_FEE;
        }
        
        emit AgentEntered(msg.sender, ENTRY_FEE, block.timestamp);
    }
    
    /**
     * @notice Distributes reward to agent for actions
     * @dev Only authorized distributors can call. Rate limited per agent.
     * @param agent Address of the agent receiving reward
     * @param amount Amount of MON to reward
     * @param reason Reason for the reward (e.g., "gathered ore")
     */
    function distributeReward(address agent, uint256 amount, string calldata reason) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        if (!isDistributor[msg.sender]) revert NotAuthorizedDistributor();
        if (!agents[agent].hasEntered) revert AgentNotInWorld();
        if (amount == 0) revert InvalidAmount();
        if (amount > MAX_REWARD_PER_ACTION) revert RewardTooLarge(amount, MAX_REWARD_PER_ACTION);
        
        // Check cooldown
        Agent storage agentData = agents[agent];
        if (block.timestamp < agentData.lastRewardTime + REWARD_COOLDOWN) {
            revert RewardCooldownActive(
                agentData.lastRewardTime + REWARD_COOLDOWN - block.timestamp
            );
        }
        
        // Mint new MON as reward
        monToken.mintReward(agent, amount);
        
        // Update agent stats
        unchecked {
            // Safe: these values won't realistically overflow
            agentData.totalEarned += amount;
            agentData.actionCount++;
            totalRewardsDistributed += amount;
        }
        agentData.lastRewardTime = block.timestamp;
        
        // Increase reputation for successful actions
        _updateReputation(agent, agentData.reputationScore + 1);
        
        emit RewardDistributed(agent, amount, reason, monToken.balanceOf(agent));
    }
    
    /**
     * @notice Transfers MON from one agent to another
     * @dev Both agents must be in the world. Protected against reentrancy.
     * @param to Recipient agent address
     * @param amount Amount of MON to transfer
     * @param reason Reason for transfer
     */
    function transferToAgent(address to, uint256 amount, string calldata reason) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        if (!agents[msg.sender].hasEntered) revert AgentNotInWorld();
        if (!agents[to].hasEntered) revert AgentNotInWorld();
        if (to == address(0)) revert InvalidAddress();
        if (amount == 0) revert InvalidAmount();
        if (monToken.balanceOf(msg.sender) < amount) revert InsufficientBalance();
        
        if (!monToken.transferFrom(msg.sender, to, amount)) revert TransferFailed();
        
        unchecked {
            // Safe: won't overflow in realistic scenarios
            agentToAgentTransfers[msg.sender][to] += amount;
            agents[msg.sender].totalSpent += amount;
        }
        
        emit AgentToAgentTransfer(msg.sender, to, amount, reason);
    }
    
    /**
     * @notice Purchases an item from another agent
     * @dev Both agents must be in the world. Protected against reentrancy.
     * @param seller Seller agent address
     * @param price Price of the item in MON
     * @param itemType Type of item being purchased
     */
    function purchaseItem(address seller, uint256 price, string calldata itemType) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        if (!agents[msg.sender].hasEntered) revert AgentNotInWorld();
        if (!agents[seller].hasEntered) revert AgentNotInWorld();
        if (seller == address(0)) revert InvalidAddress();
        if (price == 0) revert InvalidAmount();
        if (monToken.balanceOf(msg.sender) < price) revert InsufficientBalance();
        
        if (!monToken.transferFrom(msg.sender, seller, price)) revert TransferFailed();
        
        unchecked {
            // Safe: won't overflow in realistic scenarios
            agents[msg.sender].totalSpent += price;
            agents[seller].totalEarned += price;
        }
        
        // Increase seller's reputation for successful trade
        _updateReputation(seller, agents[seller].reputationScore + 2);
        
        emit ItemPurchased(msg.sender, seller, price, itemType);
    }
    
    // ============ View Functions ============
    
    /**
     * @notice Gets comprehensive agent statistics
     * @param agent Address of the agent
     * @return hasEntered Whether agent has entered the world
     * @return entryTime Timestamp when agent entered
     * @return totalEarned Total MON earned
     * @return totalSpent Total MON spent
     * @return actionCount Number of actions performed
     * @return currentBalance Current MON balance
     * @return reputationScore Agent's reputation score
     */
    function getAgentStats(address agent) external view returns (
        bool hasEntered,
        uint256 entryTime,
        uint256 totalEarned,
        uint256 totalSpent,
        uint256 actionCount,
        uint256 currentBalance,
        uint256 reputationScore
    ) {
        Agent memory a = agents[agent];
        return (
            a.hasEntered,
            a.entryTime,
            a.totalEarned,
            a.totalSpent,
            a.actionCount,
            monToken.balanceOf(agent),
            a.reputationScore
        );
    }
    
    /**
     * @notice Gets world-wide statistics
     * @return _totalAgents Total number of agents
     * @return _totalFeesCollected Total entry fees collected
     * @return _totalRewardsDistributed Total rewards distributed
     * @return treasuryBalance Current treasury balance
     */
    function getWorldStats() external view returns (
        uint256 _totalAgents,
        uint256 _totalFeesCollected,
        uint256 _totalRewardsDistributed,
        uint256 treasuryBalance
    ) {
        return (
            totalAgents,
            totalFeesCollected,
            totalRewardsDistributed,
            monToken.balanceOf(address(this))
        );
    }
    
    // ============ Internal Functions ============
    
    /**
     * @dev Updates agent reputation score
     * @param agent Address of the agent
     * @param newScore New reputation score
     */
    function _updateReputation(address agent, uint256 newScore) internal {
        uint256 oldScore = agents[agent].reputationScore;
        agents[agent].reputationScore = newScore;
        emit ReputationUpdated(agent, oldScore, newScore);
    }
}

